import './HomePage.css';
import {useEffect, useState} from 'react';


function HomePage() {

  const [serverUrl, setServerUrl] = useState("http://localhost:9000/dbTest");
  const [serverResponse, setServerResponse] = useState('');

  useEffect(() => {
    fetch(serverUrl)
    .then(res => res.text())
    .then(res => setServerResponse(res))
    .catch(error => console.log(error.message))},
     [serverUrl,serverResponse]);

  function handleSignInForm():void
  {
    //handle sign in 
    //register handler for sign in 
    console.log("User successfully signed in")

  }



  return (
  <>
    <meta charSet="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>User Authentication</title>
    <div className="container" style={{backgroundImage: "url{/smash.png}"}}>
      <h2 className="title">Welcome!</h2>
      <form action="#" method="post" onSubmit={handleSignInForm}>
        <div className="form-group">
          <label htmlFor="username">Username:</label>
          <input type="text" id="username" name="username" />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input type="password" id="password" name="password"/>
        </div>
        <div className="form-group">
          <input type="submit" defaultValue="Sign In" />
        </div>
        <div className="form-group">
          <span className="switch-text">
            <a href="TourneyMenu.html">Continue As Guest</a> {/* Guest option */}
            or
            <a href="signup.html">Sign Up Here</a> {/* Sign up option */}
          </span>
        </div>
      </form>
      </div>
  </>
  );
}

export default HomePage;
