import { useRouteError, Link} from 'react-router-dom';
import './HomePage.css';
import {useEffect, useState, ReactNode} from 'react';


export const HomePage = (props:{children?: ReactNode;}):React.JSX.Element => {

  const [serverUrl, setServerUrl] = useState("");
  const [serverResponse, setServerResponse] = useState('');

  useEffect(() => {
    fetch(serverUrl)
    .then(res => res.text())
    .then(res => setServerResponse(res))
    .catch(error => console.log(error.message))},
     [serverUrl,serverResponse]);

  function handleSignInForm():void
  {
    //handle sign in 
    //register handler for sign in 
    console.log("React handleSignInForm fired.");

  }



  return (
  <>
   <div className="container">

    <h2 className="title">Welcome!</h2>

    <form >

      <div className="form-group">

        <label htmlFor="username">Username:</label>

        <input type="text" id="username" name="username"  />

      </div>

      <div className="form-group">

        <label htmlFor="password">Password:</label>

        <input type="password" id="password" name="password"  />

        <span className="switch-text">

        <button>
          <Link style={{ color: 'white' }} to={'/menu'}>Sign In</Link>
        </button>
        <label className="form-group">Or</label>
        <button>
          <Link style={{ color: 'white' }} to={'/menu'}>Continue As Guest</Link>
        </button>
        <button>
          <Link style={{ color: 'white' }} to={`/signup`}>Sign Up Here</Link>
          </button> 

      </span>



      </div>




    </form>

</div>
  </>
  );
}


export const ErrorPage = (props:{children?: ReactNode;}):React.JSX.Element => {
  const error = useRouteError();
  console.error(error);
  return (
  <>
  <meta charSet="UTF-8" />
  <meta name="viewport" content="width=device-width, initial-scale=1.0" />
  <title>User Authentication</title>
  <div className="container">
    <h2 className="title">Oh Snap!</h2>
    <form action="#" method="post">
      <div className="form-group">
        <label htmlFor="username">There's been an error! We can't get you where you were trying to go.</label>

      </div>
    </form>
  </div>
</>

  );
}
