import { useRouteError, Link, Outlet } from "react-router-dom";
import "./HomePage.css";
import { useEffect, useState, ReactNode } from "react";

export const HomePage = (props: {
  children?: ReactNode;
}): React.JSX.Element => {
  const [serverUrl, setServerUrl] = useState("");
  const [serverResponse, setServerResponse] = useState("");

  useEffect(() => {
    fetch(serverUrl)
      .then((res) => res.text())
      .then((res) => setServerResponse(res))
      .catch((error) => console.log(error.message));
  }, [serverUrl, serverResponse]);

  function handleSignInForm(): void {
    //handle sign in
    //register handler for sign in
    console.log("React handleSignInForm fired.");
  }

  return (
    <>
      <div className="container">
        <h2 className="title">Welcome!</h2>

          <div className="form-group">
            <label htmlFor="username">Username:</label>

            <input type="text" id="username" name="username" />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password:</label>

            <input type="password" id="password" name="password" />

            <span className="switch-text">

                <Link style={{ color: "white" }} to={"/menu"}>
                  <button>
                  Sign In
                  <a href="#"></a>
                  </button>
                </Link>

              <label className="form-group">Or</label>

                <Link style={{ color: "white" }} to={"/menu"}>
                <button>
                  Continue As Guest
                  <a href="#"></a>
                </button>
                </Link>


                <Link style={{ color: "white" }} to={`/signup`}>
                <button>
                  Sign Up Here
                  <a href="#"></a>
                </button>
                </Link>

            </span>
          </div>
      </div>
      <Outlet />
    </>
  );
};

export const ErrorPage = (props: {
  children?: ReactNode;
}): React.JSX.Element => {
  const error = useRouteError();
  console.error(error);
  return (
    <>
      <meta charSet="UTF-8" />
      <meta name="viewport" content="width=device-width, initial-scale=1.0" />
      <title>Page Not Found</title>
      <div className="container">
        <h2 className="title">Oh Snap!</h2>
        <div className="form-group">
          <label>
            There's been an error! We can't get you where you were trying to go.
          </label>
        </div>
      </div>
    </>
  );
};
